    private function getArrayToSelect(array $value,array $key){
    	
    	$array = array();
    	$cant = 0;
    	foreach ($value as $v){
    		$array[$key[$cant]] = $v;
    		$cant = $cant + 1;
    	}
    	
    	return $array;
    	
    }
